import React from "react";
import styled from "styled-components";
import listToken from "../firebase/tokenList";
import ETH from "../assets/token/eth.png";
import BNB from "../assets/token/bnb.png";

const TokenRow = styled.div`
  display: flex;
  align-items: center;
  min-height: 60px;
  border-bottom: 1px solid #d9d9d9;

  .img {
    width: 32px;
    height: 32px;
    border-radius: 50%;
    border: 1px solid #333;
    padding: 4px;
    margin-left: 16px;
  }
  .name {
    font-size: 24px;
    line-height: 24px;
    font-weight: bold;
    margin-left: 8px;
  }
`;

export default function ModalToken(token) {
  return (
    <TokenRow className={`${token === "ETH" ? "ETH" : ""}`}>
      <img className="img" src={listToken[0].logo} alt="" />
      <span className="name">ETH</span>
    </TokenRow>
  );
}
