import React from "react";
import styled from "styled-components";
import { Button } from "antd";
import { user } from "../config/api.js";
import eth from "../assets/eth_logo.png";
import Asset from "../components/Asset.js";
import Activity from "../components/Activity.js";

const Wrapper = styled.div`
  width: 874px;
  margin: 32px auto 0;
  background-color: #fff;
  border-radius: 20px;
  border: 2px solid #ccc;
`;

const HeadingWrapper = styled.div`
  border-bottom: 2px solid #ccc;
  height: 84px;
  display: flex;
  align-items: center;
  justify-content: center;
  .name {
    font-size: 36px;
    font-weight: bold;
  }
`;

const FunctionWrapper = styled.div`
  height: 310px;
  display: flex;
  justify-content: flex-start;
  flex-direction: column;
  .summary {
    margin-top: 16px;
    display: flex;
    flex-direction: column;
    align-items: center;
  }
  .summary .img {
    width: 40px;
    height: 40px;
    border-radius: 50%;
    border: 1px solid #333;
    padding: 4px;
  }
  .summary .eth {
    margin: 16px 0 11px;
    font-size: 40px;
    line-height: 40px;
    font-weight: bold;
  }
  .summary .usd {
    font-size: 24px;
    line-height: 24px;
    font-weight: medium;
  }
  .function {
    margin-top: 33px;
    display: flex;
    justify-content: center;
  }
  .function .item {
    display: flex;
    flex-direction: column;
    align-items: center;
  }
  .function .item:nth-child(2) {
    margin: 0 32px;
  }
  .function .item span {
    font-size: 20px;
    font-weight: bold;
    margin-top: 3px;
  }
`;

export default function Buy() {
  return (
    <Wrapper>
      <HeadingWrapper>
        <span className="name">Mua</span>
      </HeadingWrapper>
      <FunctionWrapper>
        <div className="summary">
          <img className="img" src={eth} />
          <span className="eth">2.45 ETH</span>
          <span className="usd">$ 81.000 USD</span>
        </div>
        <div className="function">
          <div className="item">
            <Button
              type="primary"
              shape="circle"
              size="large"
              icon={<i class="fas fa-dollar-sign"></i>}
            />
            <span>Mua</span>
          </div>
          <div className="item">
            <Button
              type="primary"
              shape="circle"
              size="large"
              icon={<i class="far fa-paper-plane"></i>}
            />
            <span>Gửi</span>
          </div>
          <div className="item">
            <Button
              type="primary"
              shape="circle"
              size="large"
              icon={<i class="fas fa-sync-alt"></i>}
            />
            <span>Đổi</span>
          </div>
        </div>
      </FunctionWrapper>
    </Wrapper>
  );
}
