import React from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import "./App.css";
import "antd/dist/antd.css";
import Header from "./components/Header";
import Dashboard from "./pages/Dashboard";
import Buy from "./pages/Buy";
import Send from "./pages/Send";
import Swap from "./pages/Swap";
import Home from "./pages/Home";
import Login from "./pages/Login";
import AuthProvider from "./Context/AuthProvider";
import { useAuthState } from "./Context/AuthProvider";

const AuthenticatedRoute = ({ component: C, ...props }) => {
  const { isAuthenticated } = useAuthState();
  return (
    <Route
      {...props}
      render={(routeProps) =>
        isAuthenticated ? <C {...routeProps} /> : <Route exact path="/login" />
      }
    />
  );
};

const UnauthenticatedRoute = ({ component: C, ...props }) => {
  const { isAuthenticated } = useAuthState();
  return (
    <Route
      {...props}
      render={(routeProps) =>
        !isAuthenticated ? <C {...routeProps} /> : <Route exact path="/" />
      }
    />
  );
};

function App() {
  return (
    <div className="App">
      <AuthProvider>
        <BrowserRouter>
          <Header />
          <Routes>
            <UnauthenticatedRoute path="/home" element={<Home />} />
            <UnauthenticatedRoute path="/login" element={<Login />} />
            <AuthenticatedRoute path="/buy" element={<Buy />} />
            <AuthenticatedRoute path="/send" element={<Send />} />
            <AuthenticatedRoute path="/swap" element={<Swap />} />
            <AuthenticatedRoute path="/" element={<Dashboard />} />
          </Routes>
        </BrowserRouter>
      </AuthProvider>
    </div>
  );
}

export default App;
