import { Button } from "antd";
import React from "react";
import styled from "styled-components";
import img1 from "../assets/homepage_1.png";
import img2 from "../assets/homepage_2.png";
import img3 from "../assets/homepage_3.png";

const HomeWrapper = styled.div`
  width: 940px;
  margin: 0 auto;
`;

const RowWrapper = styled.div`
  display: flex;
  margin-bottom: 32px;
  .img {
    width: 470px;
    display: flex;
    align-items: center;
    justify-content: center;
  }
  .text {
    width: 470px;
    display: flex;
    align-items: flex-start;
    justify-content: center;
    flex-direction: column;
    .heading {
      width: 400px;
      font-size: 32px;
      line-height: 38px;
      font-weight: bold;
    }
    .description {
      width: 400px;
      margin: 16px 0 32px;
      font-size: 20px;
      line-height: 24px;
    }
    .button {
      background-color: #0074dc;
      font-size: 18px;
      font-weight: bold;
      height: fit-content;
      width: fit-content;
    }
    .button:hover {
      filter: brightness(1.1);
    }
  }
`;

export default function Home() {
  return (
    <HomeWrapper>
      <RowWrapper>
        <div className="img">
          <img src={img1} />
        </div>
        <div className="text">
          <span className="heading">
            {" "}
            Ví đồng tiền điện tử & Công nghệ Blockchain{" "}
          </span>
          <span className="description">
            {" "}
            Bắt đầu tạo ví ngay bây giờ để sở hữu những ưu đãi mới nhất{" "}
          </span>
          <Button type="primary" shape="round" className="button">
            Tạo ví mới ngay
          </Button>
        </div>
      </RowWrapper>
      <RowWrapper>
        <div className="text">
          <span className="heading">
            {" "}
            Ví đồng tiền điện tử & Công nghệ Blockchain{" "}
          </span>
          <span className="description">
            {" "}
            Bắt đầu tạo ví ngay bây giờ để sở hữu những ưu đãi mới nhất{" "}
          </span>
          <Button type="primary" shape="round" className="button">
            Tạo ví mới ngay
          </Button>
        </div>
        <div className="img">
          <img src={img1} />
        </div>
      </RowWrapper>
      <RowWrapper>
        <div className="img">
          <img src={img1} />
        </div>
        <div className="text">
          <span className="heading">
            {" "}
            Ví đồng tiền điện tử & Công nghệ Blockchain{" "}
          </span>
          <span className="description">
            {" "}
            Bắt đầu tạo ví ngay bây giờ để sở hữu những ưu đãi mới nhất{" "}
          </span>
          <Button type="primary" shape="round" className="button">
            Tạo ví mới ngay
          </Button>
        </div>
      </RowWrapper>
    </HomeWrapper>
  );
}
