import React from "react";
import styled from "styled-components";
import { Button } from "antd";
import { user } from "../config/api.js";
import eth from "../assets/eth_logo.png";
import Asset from "../components/Asset.js";
import Activity from "../components/Activity.js";

const Wrapper = styled.div`
  width: 874px;
  margin: 32px auto 0;
  background-color: #fff;
  border-radius: 20px;
  border: 2px solid #ccc;
`;

const HeadingWrapper = styled.div`
  border-bottom: 2px solid #ccc;
  height: 84px;
  display: flex;
  align-items: center;
  justify-content: center;
  .name {
    font-size: 36px;
    font-weight: bold;
  }
`;

const FunctionWrapper = styled.div`
  height: 310px;
  display: flex;
  justify-content: flex-start;
  align-items: center;
  flex-direction: column;
  .heading {
    font-size: 16px;
    font-style: italic;
    font-weight: bold;
    color: #ec1515;
    margin-top: 20px;
  }
  .usdController {
    display: flex;
    align-items: center;
    margin-top: 36px;
  }
  .usdController .money {
    font-size: 32px;
    font-weight: bold;
    margin: 0 16px;
  }
`;

export default function Buy() {
  return (
    <Wrapper>
      <HeadingWrapper>
        <span className="name">Mua</span>
      </HeadingWrapper>
      <FunctionWrapper>
        <span className="heading">Lưu ý chỉ mua được USD</span>
        <div className="usdController">
          <Button
            size="large"
            type="ghost"
            shape="circle"
            icon={<i class="fas fa-minus"></i>}
          />
          <span className="money">$0.00</span>
          <Button
            size="large"
            type="primary"
            shape="circle"
            icon={<i class="fas fa-plus"></i>}
          />
        </div>
      </FunctionWrapper>
    </Wrapper>
  );
}
