import React from "react";
import styled from "styled-components";
import ETH from "../assets/eth_logo.png";

const Row = styled.div`
  width: 100%;
  height: 100px;
  border-bottom: 2px solid #ccc;
  display: flex;
  align-items: center;
  .img {
    margin: 0 24px;
    width: 40px;
    height: 40px;
    border: 1px solid #333;
    padding: 2px;
    border-radius: 50%;
  }
  .text {
    display: flex;
    flex-direction: column;
    color: #000;
    font-size: 18px;
    font-weight: medium;
    line-height: 24px;
  }
  .text .token {
    font-size: 28px;
    line-height: 32px;
    font-weight: bold;
  }
`;

export default function Activity({ type, token, value, date }) {
  return (
    <Row>
      <img src={ETH} className="img" />
      <div className="text">
        <span className="token">
          {" "}
          {value} {token}
        </span>
        <span className="usd">$6.600 USD</span>
      </div>
    </Row>
  );
}
