import React from "react";
import styled from "styled-components";
import { user } from "../config/api.js";
import eth from "../assets/eth_logo.png";

const Wrapper = styled.div`
  width: 874px;
  height: calc(100% - 112px);
  margin: 32px auto 0;
  background-color: #fff;
  border-top-left-radius: 20px;
  border-top-right-radius: 20px;
  border: 2px solid #ccc;
`;

const PublicKey = styled.div`
  border-bottom: 2px solid #ccc;
  height: 84px;
  display: flex;
  align-items: center;
  justify-content: center;
  .info {
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
    height: 90%;
    width: 300px;
  }
  .info .name {
    font-size: 20px;
    font-weight: bold;
  }
  .info .key {
    font-size: 18;
    font-weight: medium;
  }
  .info:hover {
    width: 200px;
    border-radius: 20px;
    background: rgba(204, 204, 204, 0.25);
    display: flex;
    cursor: pointer;
  }
`;

const FunctionWrapper = styled.div`
  border-bottom: 2px solid #ccc;
  height: 310px;
  display: flex;
  justify-content: center;
  .summary {
    margin-top: 16px;
    display: flex;
    flex-direction: column;
    align-items: center;
  }
  .summary .img {
    width: 40px;
    height: 40px;
    border-radius: 50%;
    border: 1px solid #333;
    padding: 4px;
  }
  .summary .eth {
    font-size: 40px;
    line-height: 40px;
    font-weight: bold;
  }
  .summary .usd {
    font-size: 24px;
    font-weight: medium;
  }
`;

const AAWrapper = styled.div`
  display: flex;
  flex-direction: row;
  .title {
    font-size: 20px;
    font-weight: medium;
    line-height: 24px;
    color: #666;
    width: calc(100% / 2);
    border-bottom: 2px solid #ccc;
    height: 52px;
    display: flex;
    justify-content: center;
    align-items: center;
  }
  .title.active {
    color: #000;
    font-weight: bold;
    border-bottom: 4px solid #000;
  }
`;

export default function Dashboard() {
  return (
    <Wrapper>
      <PublicKey>
        <div className="info">
          <span className="name">{user.name}</span>
          <span className="key">
            0x{user.publicKey.substring(0, 4)}...
            {user.publicKey.substring(30, 34)}
          </span>
        </div>
      </PublicKey>
      <FunctionWrapper>
        <div className="summary">
          <img className="img" src={eth} />
          <span className="eth">2.45 ETH</span>
          <span className="usd">$ 81.000 USD</span>
        </div>
        <div></div>
      </FunctionWrapper>
      <AAWrapper>
        <div className="title active">Tài sản</div>
        <div className="title">Hoạt động</div>
      </AAWrapper>
    </Wrapper>
  );
}
