import React from "react";
import styled from "styled-components";
import { Button } from "antd";
import { Input } from "antd";

const Wrapper = styled.div`
  width: 874px;
  margin: 32px auto 0;
  background-color: #fff;
  border-radius: 20px;
  border: 2px solid #ccc;
  box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.2);
`;

const HeadingWrapper = styled.div`
  position: relative;
  border-bottom: 2px solid #ccc;
  height: 84px;
  display: flex;
  align-items: center;
  justify-content: center;
  .name {
    font-size: 36px;
    font-weight: bold;
  }
  .back {
    position: absolute;
    right: 32px;
    font-size: 24px;
    color: #0074dc;
    border-bottom: 1px solid #0074dc;
    cursor: pointer;
  }
  .back .back-icon {
    margin-right: 4px;
  }
  .back:hover {
    filter: brightness(1.4);
  }
`;

const FunctionWrapper = styled.div`
  height: 310px;
  display: flex;
  justify-content: flex-start;
  align-items: center;
  flex-direction: column;
  .heading {
    font-size: 16px;
    font-style: italic;
    font-weight: bold;
    color: #ec1515;
    margin-top: 20px;
  }
  .usdController {
    display: flex;
    align-items: center;
    margin-top: 36px;
  }
  .usdController .money {
    font-size: 40px;
    font-weight: bold;
    margin: 0 16px;
  }
  .complete {
    margin-top: 42px;
  }
`;

const InputWrapper = styled.div`
  position: relative;
  margin-top: 32px;
  .type {
    position: absolute;
    z-index: 99;
    top: -12px;
    left: 24px;
    height: 24px;
    background-color: #1890ff;
    padding: 0 24px;
    font-size: 16px;
    color: #fff;
    border-radius: 20px;
  }
  .inputType {
    width: 315px;
    height: 60px;
    border-radius: 20px;
    font-size: 20px;
  }
`;

const TokenInputWrapper = styled.div`
  position: relative;
  margin-top: 32px;
  .inputType {
    width: 315px;
    height: 60px;
    border-radius: 20px;
    font-size: 20px;
  }
  .inputType::placeholder {
    padding-left: 100px;
  }
  .inputType::value {
    padding-left: 100px;
  }
`;

export default function Send() {
  return (
    <Wrapper>
      <HeadingWrapper>
        <span className="name">Gửi</span>
        <span className="back">
          <i class="fas fa-angle-left back-icon"></i>Quay lại
        </span>
      </HeadingWrapper>
      <FunctionWrapper>
        <InputWrapper>
          <div className="type">Gửi đến ví</div>
          <Input className="inputType" placeholder="Số lượng" />
        </InputWrapper>
        <TokenInputWrapper>
          <Input className="inputType" placeholder="Số lượng Token" />
        </TokenInputWrapper>
        <Button size="large" type="primary" shape="round" className="complete">
          Gửi
        </Button>
      </FunctionWrapper>
    </Wrapper>
  );
}
