import React from "react";
import styled from "styled-components";
import logoName from "../assets/logo_name.png";
import { Avatar, Button } from "antd";
import { Link, useLocation } from "react-router-dom";

const Wrapper = styled.div`
  height: 80px;
  width: 940px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin: 0 auto;
  .header {
    .img {
      cursor: pointer;
    }
    &__left {
      display: flex;
      align-items: center;
      .home {
        &__text {
          color: #0074dc;
          font-size: 18px;
          line-height: 18px;
          border-bottom: 2px solid #0074dc;
          font-weight: 600;
        }
        &__button {
          background-color: #0074dc;
          font-size: 20px;
          font-weight: bold;
          width: fit-content;
          height: fit-content;
          margin-left: 16px;
        }
      }
    }
  }
  .button.disabled {
    display: none;
  }
  .userLogo.disabled {
    display: none;
  }
`;

export default function Header() {
  let location = useLocation();
  React.useEffect(() => {
    console.log(location.pathname);
    document
      .querySelector(".header .userLogo")
      .classList.toggle("disabled", location.pathname === "/home");
    document
      .querySelector(".header .button")
      .classList.toggle("disabled", location.pathname === "/home");
  }, [location]);
  return (
    <div style={{ width: "100%" }}>
      <Wrapper className="header">
        <Link to="/">
          <img src={logoName} style={{ height: "60px" }} className="img" />
        </Link>
        <div className="header__left">
          <div className="home">
            <span className="home__text">Đã có ví ?</span>
            <Button className="home__button" type="primary" shape="round">
              Tạo ví mới
            </Button>
          </div>
          <div>
            <Button type="ghost" shape="round" className="button">
              Etherum Mainnet
            </Button>
            <Avatar
              className="userLogo"
              size={40}
              icon={<i className="fas fa-user"></i>}
            />
          </div>
        </div>
      </Wrapper>
    </div>
  );
}
